import React, { useEffect, useState } from "react";
import logo from "./logo.svg";
import "./App.css";
import SearchPanel from "./components/SearchPanel";
import { Container, Grid } from "@mui/material";
import axios from "axios";
import Images from "./components/Images";
import UploadImage from "./components/UploadImage";
import Navbar from "./components/Navbar";

function App() {
  const [content, setContent] = useState("");
  const [images, setImages] = useState<String[]>([
    "https://hw3-images.s3.amazonaws.com/test.jpg",
    "https://hw3-images.s3.amazonaws.com/test.jpg",
    "https://hw3-images.s3.amazonaws.com/test.jpg",
    "https://hw3-images.s3.amazonaws.com/test.jpg",
    "https://hw3-images.s3.amazonaws.com/test.jpg",
    "https://hw3-images.s3.amazonaws.com/test.jpg",
    "https://hw3-images.s3.amazonaws.com/test.jpg",
    "https://hw3-images.s3.amazonaws.com/test.jpg",
    "https://hw3-images.s3.amazonaws.com/test.jpg",
    "https://hw3-images.s3.amazonaws.com/test.jpg",
    "https://hw3-images.s3.amazonaws.com/test.jpg",
    "https://hw3-images.s3.amazonaws.com/test.jpg",
  ]);

  const searchContent = (searchContent: string) => {
    setContent(searchContent);
    console.log("Searching " + content);
  };

  useEffect(() => {
    axios
      .get("api-gateway-api/search?q=" + content)
      .then((response) => {
        console.log(response.data);
        setImages([]);
      })
      .catch(() => {
        console.log("Error");
      });
  }, [content]);

  return (
    <>
      <Navbar />
      <Container maxWidth="md">
        <Grid
          container
          spacing={2}
          display={"flex"}
          justifyContent={"center"}
          alignItems={"center"}
        >
          <Grid
            item
            xs={10}
            display={"flex"}
            justifyContent={"center"}
            alignItems={"center"}
          >
            <SearchPanel searchContent={searchContent} />
          </Grid>
          <Grid
            item
            xs={12}
            display={"flex"}
            justifyContent={"center"}
            alignItems={"center"}
          >
            <UploadImage />
          </Grid>
          <Grid
            item
            xs={12}
            display={"flex"}
            justifyContent={"center"}
            alignItems={"center"}
          >
            <Images images={images} />
          </Grid>
        </Grid>
      </Container>
    </>
  );
}

export default App;
